package ${packageName}.${moduleName}.web<#if subModuleName != "">.${subModuleName}</#if>;

import java.util.List;
import java.util.Map;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import org.apache.shiro.authz.annotation.Logical;
import org.apache.shiro.authz.annotation.RequiresPermissions;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.ModelAttribute;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.ResponseBody;
import org.springframework.web.servlet.mvc.support.RedirectAttributes;

import com.google.common.collect.Lists;
import com.google.common.collect.Maps;
import com.jeespring.common.config.Global;
import com.jeespring.common.web.AbstractBaseController;
import com.jeespring.common.utils.StringUtils;
import ${packageName}.${moduleName}.entity<#if subModuleName != "">.${subModuleName}</#if>.${ClassName}Tree;
import ${packageName}.${moduleName}.service<#if subModuleName != "">.${subModuleName}</#if>.${ClassName}TreeService;

/**
 * ${functionName}Controller
 * @author ${functionAuthor}
 * @version ${functionVersion}
 */
@Controller
@RequestMapping(value = "${r"${adminPath}"}/${urlPrefix}Tree")
public class ${ClassName}TreeController extends AbstractBaseController {

	@Autowired
	private ${ClassName}TreeService ${className}TreeService;

	@ModelAttribute
	public ${ClassName}Tree get(@RequestParam(required=false) String id) {
		${ClassName}Tree entity = null;
		if (StringUtils.isNotBlank(id)){
			entity = ${className}TreeService.get(id);
		}
		if (entity == null){
			entity = new ${ClassName}Tree();
		}
		return entity;
	}

	/**
	 * ${functionNameSimple}列表页面
	 */
	//@RequiresPermissions("${permissionPrefix}:list")
	@RequestMapping(value = {"list", ""})
	public String list(${ClassName}Tree ${className}, HttpServletRequest request, HttpServletResponse response, Model model) {
		List<${ClassName}Tree> list = ${className}TreeService.findList(${className});
		model.addAttribute("list", list);
		return "${lastPackageName}/${viewPrefix}TreeList";
	}

	/**
	 * 查看，增加，编辑${functionNameSimple}表单页面
	 */
	//@RequiresPermissions(value={"${permissionPrefix}:view","${permissionPrefix}:add","${permissionPrefix}:edit"},logical=Logical.OR)
	@RequestMapping(value = "form")
	public String form(${ClassName}Tree ${className}, Model model) {
		if (${className}.getParent()!=null && StringUtils.isNotBlank(${className}.getParent().getId())){
			${className}.setParent(${className}TreeService.get(${className}.getParent().getId()));
			// 获取排序号，最末节点排序号+30
			if (StringUtils.isBlank(${className}.getId())){
				${ClassName}Tree ${className}Child = new ${ClassName}Tree();
				${className}Child.setParent(new ${ClassName}Tree(${className}.getParent().getId()));
				List<${ClassName}Tree> list = ${className}TreeService.findList(${className});
				if (list.size() > 0){
					${className}.setSort(list.get(list.size()-1).getSort());
					if (${className}.getSort() != null){
						${className}.setSort(${className}.getSort() + 30);
					}
				}
			}
		}
		if (${className}.getSort() == null){
			${className}.setSort(30);
		}
		model.addAttribute("${className}", ${className});
		return "${lastPackageName}/${viewPrefix}TreeForm";
	}

	/**
	 * 保存${functionNameSimple}
	 */
	//@RequiresPermissions(value={"${permissionPrefix}:add","${permissionPrefix}:edit"},logical=Logical.OR)
	@RequestMapping(value = "save")
	public String save(${ClassName}Tree ${className}, Model model, RedirectAttributes redirectAttributes) {
		if (!beanValidator(model, ${className})){
			return form(${className}, model);
		}
		${className}TreeService.save(${className});
		addMessage(redirectAttributes, "保存${functionNameSimple}成功");
		return "redirect:"+Global.getAdminPath()+"/${viewPrefix}Tree/?repage";
	}

	/**
	 * 删除${functionNameSimple}
	 */
	//@RequiresPermissions("${permissionPrefix}:del")
	@RequestMapping(value = "delete")
	public String delete(${ClassName}Tree ${className}, RedirectAttributes redirectAttributes) {
		${className}TreeService.delete(${className});
		addMessage(redirectAttributes, "删除${functionNameSimple}成功");
		return "redirect:"+Global.getAdminPath()+"/${viewPrefix}Tree/?repage";
	}

	//@RequiresPermissions("user")
	@ResponseBody
	@RequestMapping(value = "treeData")
	public List<Map<String, Object>> treeData(@RequestParam(required=false) String extId, HttpServletResponse response) {
		List<Map<String, Object>> mapList = Lists.newArrayList();
		List<${ClassName}Tree> list = ${className}TreeService.findList(new ${ClassName}Tree());
		for (int i=0; i<list.size(); i++){
			${ClassName}Tree e = list.get(i);
			if (StringUtils.isBlank(extId) || (extId!=null && !extId.equals(e.getId()) && e.getParentIds().indexOf(","+extId+",")==-1)){
				Map<String, Object> map = Maps.newHashMap();
				map.put("id", e.getId());
				map.put("pId", e.getParentId());
				map.put("name", e.getName());
				mapList.add(map);
			}
		}
		return mapList;
	}

}
